void speaker_callback(speaker_event_t event)
{
     LOG_INF("speaker_callback(%d)", event);
     sound_finished = true;
}

void main(void)
{
    const struct device *dev;

    if (IS_ENABLED(CONFIG_LOG_BACKEND_RTT)) {
        /* Give RTT log time to be flushed before executing tests */
        k_sleep(K_MSEC(500));
    }

    dev = device_get_binding("thingy_speaker");
    if (dev == NULL) {
        LOG_ERR("Failed to get speaker dev binding");
        return;
    }
    LOG_INF("Speaker dev is %p, name is %s", dev, dev->name);

    int err = speaker_pcm_play(dev, sound_0, sizeof(sound_0), speaker_callback);
    if (err) {
      LOG_ERR("speaker_tone_play failed: %d", err);
    }

    for (int i=0; i<15 ; i++) {
      sound_finished = false;
      int err = speaker_tone_play(dev, 200 * (i + 2), 500, 100, speaker_callback);
      if (err) {
        LOG_ERR("speaker_pcm_play failed: %d", err);
      }
      while(!sound_finished) {}
    }

    while (1) {
        k_sleep(K_MSEC(100));
    }
}
